import React, { Component } from 'react'
import { Table, Row, Form } from 'react-bootstrap'
import axios from 'axios';
class ClassDetail extends Component {
	
	constructor(props) {
		super(props);
		this.state = {
			subjectdetail: {}
		}
	}

	componentDidMount() {
		axios({
			method: 'GET',
			url: 'http://localhost:5000/api/subjects/detail-list',
			data: null
		}).then(res => {
			console.log(res);
			this.setState({
				subjectdetails: res.data
			});
		}).catch(err => {
			console.log(err);
		})
	}

	showSubjects = (subjectdetail) => {
		var result = null;
		if (subjectdetail) {
			result = subjectdetail.map((subject, index) => {
				return (
					<tr>
						<td>{index + 1}</td>
						<td>{subject._id}</td>
						<td>{subject.MonHoc.TenMH}</td>
						<td>{subject.KhoaHoc}</td>
						<td>{subject.GiangVien.People.HoTen}</td>
						<td style={{ textAlign: "right" }}>
							{/* <Link to={"/chitietlophoc/" + subject._id} >Xem chi tiết</Link> */}
						</td>
					</tr>
				)
			});
		}
		return result;
	}
	
	render() {
		return (
			<>
				<Row style={{ borderBottom: '1px solid black' }}>
					<h4>Lớp A</h4>
				</Row>

				<Row style={{ marginTop: '2rem', display: 'flex', flexDirection: 'column' }}>
					<h4>Thông tin chung</h4>
					<Form inline>
						<Form.Group>
							<Form.Label>Giảng viên phụ trách</Form.Label>
							<Form.Control style={{ marginLeft: '2rem' }} type="text" placeholder="Trần Văn A" readOnly />
						</Form.Group>
					</Form>
					<Form inline>
						<Form.Group>
							<Form.Label>Mã lớp: </Form.Label>
							<Form.Control style={{ marginLeft: '2rem' }} type="text" placeholder="ABC123" readOnly />
						</Form.Group>
					</Form>

				</Row>

				<Row style={{ marginTop: '2rem' }}>
					<h4>Danh sách học sinh</h4>
				</Row>

				<Row style={{ marginTop: '1rem' }}>
					<form className="form-inline" style={{ marginLeft: "75%" }}>
						<div className="form-group mx-sm-3 mb-2">
							<label for="inputPassword2" className="sr-only">Nhập tên học sinh</label>
							<input type="text" className="form-control" id="inputPassword2" placeholder="Nhập tên học sinh" />
						</div>
						<button type="submit" className="btn btn-primary mb-2">TÌM KIẾM</button>
					</form>
					<Table bordered >
						<thead>
							<tr>
								<th>STT</th>
								<th>Họ và tên</th>
								<th>MSSV</th>
								<th>Cập nhật</th>
							</tr>
						</thead>
						<tbody>
							<tr>
								<td>1</td>
								<td> Nguyễn Thị O </td>
								<td> 19DH110855</td>
								<td> <a href="#">Delete</a> <a href="#">Edit</a> </td>
							</tr>
							<tr>
								<td>1</td>
								<td> Nguyễn Thị C </td>
								<td> 19DH110856</td>
								<td> <a href="#">Delete</a> <a href="#">Edit</a> </td>
							</tr>
							<tr>
								<td>1</td>
								<td> Nguyễn Thị B </td>
								<td> 19DH110857</td>
								<td> <a href="#">Delete</a> <a href="#">Edit</a> </td>
							</tr>
						</tbody>
					</Table>
				</Row>
			</>
		)
	}
}
export default ClassDetail;
