import React, { Component } from "react";
import SearchBar from "../../components/SearchBar"
import { Table } from "react-bootstrap"
import axios from 'axios'
class SubjectManagement extends Component {

	constructor(props) {
		super(props);
		this.state = {
			subjects: []
		}
	}

	componentDidMount() {
		axios({
			method: 'GET',
			url: 'http://localhost:5000/api/subjects',
			data: null
		}).then(res => {
			console.log(res);
			this.setState({
				subjects: res.data
			});
		}).catch(err => {
			console.log(err);
		})
	}

	showSubjects = (subjects) => {
		var result = null;
		if (subjects.length > 0) {
			result = subjects.map((subject, index) => {
				return (
					<tr>
						<td>{index + 1}</td>
						<td>{subject.TenMH}</td>
						<td style={{ textAlign: "right" }}><a href="/thoikhoabieu">Xem chi tiết</a></td>
					</tr>
				)
			});
		}
		return result;
	}

	render() {
		return (
			<div>
				<h2>Danh sách môn học</h2>
				<SearchBar />
				<Table striped bordered hover variant="dark" style={{ width: "100%", marginTop: "1rem" }} >
					<thead>
						<tr>
							<th>STT</th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<td>1</td>
							<td>DLCMDCSVN</td>
							<td style={{ textAlign: "right" }}><a href="/thoikhoabieu">Xem chi tiết</a></td>
						</tr>
						<tr>
							<td>2</td>
							<td>Toán cao cấp</td>
							<td style={{ textAlign: "right" }}><a href="/thoikhoabieu">Xem chi tiết</a></td>
						</tr>
						<tr>
							<td>3</td>
							<td>Giải phẫu</td>
							<td style={{ textAlign: "right" }}><a href="/thoikhoabieu">Xem chi tiết</a></td>
						</tr>
						<tr>
							<td>4</td>
							<td>Đại số tuyến tính</td>
							<td style={{ textAlign: "right" }}><a href="/thoikhoabieu">Xem chi tiết</a></td>
						</tr>

					</tbody>
				</Table>
			</div>
		)
	}

}
export default SubjectManagement;
